import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  data: '',
  author: '',
  type: true,
  status: true,
}

const filterSlice = createSlice({
  name: 'filter',

  initialState,

  reducers: {
    setDataFilter: (state, action) => {
      state.title = action.payload
    },
    setAuthorFilter: (state, action) => {
      state.author = action.payload
    },
    setTypeFilter: (state, action) => {
      state.type = action.payload
    },
    setStatusFilter: (state, action) => {
      state.status = action.payload
    },

    resetFilters: () => {
      return initialState
    },
  },
})

export const {
  setDataFilter,
  setAuthorFilter,
  setTypeFilter,
  setStatusFilter,
  resetFilter,
} = filterSlice.actions
// export const setTitleFilter = filterSlice.actions.setTitleFilter
// export const resetFilters = filterSlice.actions.resetFilters
// export const setAuthorFilter = filterSlice.actions.setAuthorFilter
// export const setOnlyFavoriteFilter = filterSlice.actions.setOnlyFavoriteFilter

export const selectTitleFilter = (state) => {
  return state.filterSlice.title
}

export const selectAuthorFilter = (state) => {
  return state.filterSlice.author
}

export const selectTypeFilter = (state) => {
  return state.filterSlice.type
}

export const selectStatusFilter = (state) => {
  return state.filterSlice.status
}

// Импортируем редьюсер
export default filterSlice.reducer
