import { createSlice } from '@reduxjs/toolkit'

const initialState = []

const itemPageSlice = createSlice({
  name: 'itemPageSlice',
  initialState,
  reducers: {
    addItem: (state, action) => {
      state = action.payload
    },
  },
})

export const { addItem } = itemPageSlice.actions

export const selectAddItem = (state) => state.itemPageSlice
export default itemPageSlice.reducer
