import React from 'react'
import styles from './InfoPage.module.css'
import { selectItem } from '../State/itemSlice'
import { useSelector } from 'react-redux'

function InfoPage({ setVisibleInfo, data }) {
  const item = useSelector(selectItem)

  const dataItem = data.filter((_, id) => id === item)[0]

  return (
    <div className={styles.container} onClick={() => setVisibleInfo(false)}>
      <div className={styles.content_container}>
        <div className={styles.content__name}>{`${dataItem.userName}`}</div>
        <div
          className={styles.content__type}
          style={{
            backgroundColor: `${
              dataItem.typeRequest === 'В работе'
                ? '#b3dafa89'
                : dataItem.typeRequest === 'В очереди'
                ? '#dfca628f'
                : '#59c6a582'
            }`,
            color: `${
              dataItem.typeRequest === 'В работе'
                ? '#0079C2'
                : dataItem.typeRequest === 'В очереди'
                ? '#a9994c'
                : '#45987f'
            }`,
          }}
        >{`${dataItem.typeRequest}`}</div>
      </div>
    </div>
  )
}

export default InfoPage
