import styles from './Modal.module.css'
import Vector from '../img/Vector.svg'
import { useState } from 'react'
import Button from '../UI/Button'
// onClick={() => setVisibleModal(false)}
function Modal({ setVisibleModal }) {
  const [visibleList, setVisibleList] = useState(false)
  const [nameInput, setNameInput] = useState('')
  const [descTextarea, setDescTextarea] = useState('')
  const [nameDirty, setNameDirty] = useState(false)
  const [descDirty, setDescDirty] = useState(false)
  const [nameError, setNameError] = useState('Поле не может быть пустым')
  const [descError, setDescError] = useState('Поле не может быть пустым')

  // const [imgForm, setImgFotm] = useState(null)

  return (
    <div className={styles.container}>
      <form>
        <div className={styles.form_item}>
          {nameDirty && nameError && (
            <div style={{ color: 'red' }}>{nameError}</div>
          )}
          <h2>Автор обращения</h2>
          <input
            type="text"
            placeholder="Имя Фамилия"
            className={styles.form_item__element}
          />
        </div>
        <div
          className={styles.form_item__type}
          onClick={() => setVisibleList(!visibleList)}
        >
          <div className={styles.form_item}>
            <h2>Тип запроса</h2>
            <div
              className={`${styles.form_item__element} ${styles.form_item__type_element}`}
            >
              <span>Ошибка</span>
              <img src={Vector} alt="Vector" width={`13px`} height={`8px`} />
            </div>
          </div>
          {visibleList && (
            <div className={styles.form_item__type_open}>
              <div className={styles.type_open__item}>Ошибка</div>
              <div className={styles.type_open__item}>
                Новая функциональность
              </div>
              <div className={styles.type_open__item}>Улучшение</div>
              <div className={styles.type_open__item}>Документация</div>
            </div>
          )}
        </div>
        <div className={styles.form_item__description}>
          <h2>Добавить описание</h2>
          <textarea placeholder="Введите описание запроса" />
        </div>
        <div className={styles.form_item__document_input}>
          <h2>Добавить изображение</h2>
          <input
            type="file"
            // value={imgForm}
            // onChange={(e) => setImgFotm(e.target.value)}
          />
        </div>
        <div className={styles.form_item__btn_container}>
          <span>
            <Button
              text={`Сохранить`}
              width={123}
              height={40}
              type={`submit`}
            />
          </span>
          <span onClick={() => setVisibleModal(false)}>
            <Button text={`Закрыть`} width={109} height={40} />
          </span>
        </div>
      </form>
    </div>
  )
}

export default Modal
