import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {}

// запрос из бд
export const fetchData = createAsyncThunk(
  'dataSlice/fetchData',
  async (url) => {
    try {
      const res = await axios.get(url)
      return res.data
    } catch (error) {
      console.log(error)
    }
  }
)

const dataSlice = createSlice({
  name: 'dataSlice',
  initialState,
  reducers: {
    addData: (state, action) => {
      state.push(action.payload)
    },
    // changeDay: (state, action) => {
    //   let id = state.findIndex((el) => el.id === action.payload.id)
    //   state.splice(id, 1, action.payload)
    // },
    // deleteDay: (state, action) => {
    //   return state.filter((elem) => elem.id !== action.payload)
    // },
  },
  // Редьюсер для асинхронных запросов 1
  extraReducers: (builder) => {
    // первым аргументом идёт фулфилд запроса, вторым - редьюсер после успешного получения данных (изменение state)
    builder.addCase(fetchData.fulfilled, (state, action) => {
      console.log(action.payload)
      return (state = action.payload)
    })
  },
})

export const { addData } = dataSlice.actions

export const selectData = (state) => state.dataSlice
export default dataSlice.reducer
