import styles from './MainPage.module.css'
import Header from './Header'
import Table from './Table'
import Modal from './Modal'
import { useState } from 'react'
import { useSelector } from 'react-redux'
import { selectData } from '../State/dataSlice'
import {
  selectAuthorFilter,
  selectDatafilter,
  selectStatusFilter,
  selectTypeFilter,
  setAuthorFilter,
  setDataFilter,
  setStatusFilter,
  setTypeFilter,
} from '../State/filterSlice'
import { dataReBuild } from '../UI/dataReBuild'

function MainPage() {
  const dataInfo = useSelector(selectData)
  const dataFilter = useSelector(selectDatafilter)
  const authorFilter = useSelector(selectAuthorFilter)
  const typeFilter = useSelector(selectTypeFilter)

  const filteredInfo = dataInfo.filter((dataInfo) => {
    const matchesData = dataReBuild(new Date(dataInfo.data))
      .toLowerCase()
      .includes(dataFilter.toLowerCase())
    const matchesAuthor = dataInfo.userName
      .toLowerCase()
      .includes(authorFilter.toLowerCase())
    // // тут мы фильруем объекты по свойству isFavorite
    // // если оно true, то мы показываем те книги, в которых book.isFavorite = true
    // // если оно false то мы устанавливаем всем книгам значение true и они попадают в итоговый массив
    // const matchesFavorite = onlyFavorite ? book.isFavorite : true
    // Фильтр по всем показателям
    return matchesData && matchesAuthor && dataInfo.typeRequest === !!typeFilter
      ? typeFilter
      : true
  })

  const [visibleModal, setVisibleModal] = useState(false)

  const [start, setStart] = useState(0)
  const [end, setEnd] = useState(12)

  return (
    <>
      <div className={visibleModal ? styles.container_none : styles.container}>
        <Header
          setVisibleModal={setVisibleModal}
          setStart={setStart}
          setEnd={setEnd}
          start={start}
          end={end}
          data={filteredInfo}
        />
        <Table start={start} end={end} dataInfo={filteredInfo} />
      </div>
      {visibleModal ? <Modal setVisibleModal={setVisibleModal} /> : false}
    </>
  )
}

export default MainPage
