import { dataReBuild } from '../UI/dataReBuild'
import styles from './TableItem.module.css'
import {
  selectAuthorFilter,
  selectDatafilter,
  selectStatusFilter,
  selectTypeFilter,
} from '../State/filterSlice'
import { useSelector } from 'react-redux'

function TableItem({ info }) {
  const dataFilter = useSelector(selectDatafilter)
  const authorFilter = useSelector(selectAuthorFilter)
  const typeFilter = useSelector(selectTypeFilter)
  const statusFilter = useSelector(selectStatusFilter)

  let reBuildData = dataReBuild(new Date(info.data))

  const filteredInfo = info.filter((info) => {
    const matchesData = reBuildData
      .toLowerCase()
      .includes(titleFilter.toLowerCase())
    const matchesAuthor = book.author
      .toLowerCase()
      .includes(authorFilter.toLowerCase())
    // тут мы фильруем объекты по свойству isFavorite
    // если оно true, то мы показываем те книги, в которых book.isFavorite = true
    // если оно false то мы устанавливаем всем книгам значение true и они попадают в итоговый массив
    const matchesFavorite = onlyFavorite ? book.isFavorite : true
    // Фильтр по всем показателям
    return matchesAuthor && matchesTitle && matchesFavorite
  })

  return (
    <div className={styles.table_items}>
      <div
        className={`${styles.table_items__number} ${styles.table_items__container}`}
      >
        <h2>{info.number}</h2>
      </div>
      <div
        className={`${styles.table_items__type_container} ${styles.table_items__container}`}
      >
        <div className={styles.table_items__type}>
          <h2
            style={{
              backgroundColor: `${
                info.typeRequest === 'Ошибка'
                  ? '#fd1e007c'
                  : info.typeRequest === 'Новая функциональность'
                  ? '#59c6a589'
                  : info.typeRequest === 'Улучшение'
                  ? '#3973ae89'
                  : '#aea64283'
              }`,
              color: `${
                info.typeRequest === 'Ошибка'
                  ? '#620b00'
                  : info.typeRequest === 'Новая функциональность'
                  ? '#418c75'
                  : info.typeRequest === 'Улучшение'
                  ? '#004488'
                  : '#7d7831'
              }`,
            }}
          >
            {info.typeRequest}
          </h2>
        </div>
      </div>
      <div className={`${styles.table_items__container}`}>
        <div className={`${styles.table_items__description}`}>
          {info.description}
        </div>
      </div>
      <div
        className={`${styles.table_items__usersData} ${styles.table_items__container}`}
      >
        {info.userName}
      </div>
      <div
        className={`${styles.table_items__usersData} ${styles.table_items__container}`}
      >
        {reBuildData}
      </div>
      <div className={`${styles.table_items__container}`}>
        <div className={`${styles.table_items__status_work}`}>
          <h2
            style={{
              backgroundColor: `${
                info.status === 'В работе'
                  ? '#b3dafa89'
                  : info.status === 'В очереди'
                  ? '#dfca628f'
                  : '#59c6a582'
              }`,
              color: `${
                info.status === 'В работе'
                  ? '#0079C2'
                  : info.status === 'В очереди'
                  ? '#a9994c'
                  : '#45987f'
              }`,
            }}
          >
            {info.status}
          </h2>
        </div>
      </div>
    </div>
  )
}

export default TableItem
